-- create database BandChess;

use BandChess;
set foreign_key_checks = 1;
truncate usuario;
select * from usuario;
CREATE TABLE Usuario (
  idUsuario INT primary key auto_increment,
  email VARCHAR(45) NOT NULL,
  nickname VARCHAR(20) NOT NULL,
  senha VARCHAR(45) NOT NULL,
  rating INT(5),
  AberturaFavoritaBrancas char(3),
  AberturaFavoritaPretas char(3),
  plataforma VARCHAR(10),
  tituloFIDE CHAR(2) check (tituloFIDE in('NO','GM', 'IM', 'NM')),
  idFIDE int(15)
);

CREATE TABLE Abertura(
  codigoECO VARCHAR(45) primary key,
  nome VARCHAR(100)
  );

CREATE TABLE Comentario (
  idComentario INT primary key auto_increment,
  fkUsuario int not null,
  textoComentario VARCHAR(500) NOT NULL,
  foreign key (fkUsuario) references Usuario(idUsuario)
  );

create table Torneio(
idTorneio int primary key auto_increment,
nomeTorneio varchar(45),
dataTorneio date,
campeao varchar(45),
segundo varchar(45),
terceiro varchar(45),
quarto varchar(45),
quinto varchar(45)
);

create table Desempenho(
fkUsuario int,
fkTorneio int,
posicao int,
vitorias int,
empates int,
derrotas int,
foreign key (fkUsuario) references Usuario(idUsuario),
foreign key (fkTorneio) references Torneio(idTorneio),
primary key (fkUsuario, fkTorneio)
);

